        @keyframes slideRight {
          from {
            opacity: 0;
            transform: translateX(-50px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes slideLeft {
          from {
            opacity: 0;
            transform: translateX(50px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes zoomIn {
          from {
            opacity: 0;
            transform: scale(0.8);
          }
          to {
            opacity: 1;
            transform: scale(1);
          }
        }

        @keyframes zoomInUp {
          from {
            opacity: 0;
            transform: translateY(30px) scale(0.8);
          }
          to {
            opacity: 1;
            transform: translateY(0) scale(1);
          }
        }

        @keyframes zoomInDown {
          from {
            opacity: 0;
            transform: translateY(-30px) scale(0.8) rotate(30deg);
          }
          to {
            opacity: 1;
            transform: translateY(0) scale(1) rotate(30deg);
          }
        }

        @keyframes flipUp {
          from {
            opacity: 0;
            transform: perspective(400px) rotateX(-90deg);
          }
          to {
            opacity: 1;
            transform: perspective(400px) rotateX(0deg);
          }
        }

        @keyframes flipDown {
          from {
            opacity: 0;
            transform: perspective(400px) rotateX(90deg) rotate(20deg);
          }
          to {
            opacity: 1;
            transform: perspective(400px) rotateX(0deg) rotate(20deg);
          }
        }

        @keyframes float {
          0%,
          100% {
            transform: translateY(0px);
          }
          50% {
            transform: translateY(-20px);
          }
        }

        @keyframes pulse {
          0%,
          100% {
            transform: scale(1);
          }
          50% {
            transform: scale(1.05);
          }
        }

        /* Typing cursor animation */
        .typing-cursor {
          animation: blink 1s infinite;
        }

        @keyframes blink {
          0%,
          50% {
            opacity: 1;
          }
          51%,
          100% {
            opacity: 0;
          }
        }

        /* Hover effects */
        .btn {
          transition: all 0.3s ease;
        }

        .btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(255, 193, 7, 0.3) !important;
        }

        .btn-outline-light:hover {
          background-color: rgba(255, 255, 255, 0.1);
          border-color: white;
          box-shadow: 0 8px 20px rgba(255, 255, 255, 0.2) !important;
        }

        .img-fluid:hover {
          transform: scale(1.05);
          transition: transform 0.3s ease;
        }

        /* Responsive adjustments */
        @media (max-width: 768px) {
          .hero {
            padding: 20px 0;
          }

          .container {
            padding-left: 20px;
            padding-right: 20px;
          }

          .floating-shape {
            display: none;
          }

          .display-4 {
            font-size: 2rem;
          }

          .h3 {
            font-size: 1.5rem;
          }
        }

        @media (min-width: 769px) and (max-width: 1024px) {
          .display-4 {
            font-size: 2.5rem;
          }

          .floating-shape {
            transform: scale(0.8);
          }
        }

        /* Enhanced mouse scroll indicator */
        .mouse-svg:hover {
          transform: scale(1.1);
          transition: transform 0.3s ease;
        }

        .aos-animate {
          transition-property: transform, opacity;
          transition-duration: 0.6s;
          transition-timing-function: ease;
        }
      `}</style>
